int gevFree   (gevHandle_t *pgev)
{
  assert(gevXFree);
  gevXFree(pgev); pgev = NULL;
  objectCount--;
  return 1;
} /* gevFree */

int gevLibraryLoaded(void)
{
  return isLoaded;
} /* gevLibraryLoaded */

int gevLibraryUnload(void)
{
  if (objectCount > 0)
    return 0;
  isLoaded = 0;
  (void) unLoadLib(h);
  return 1;
} /* gevLibraryUnload */

